import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { Container, Navbar, Nav } from 'react-bootstrap'
import './App.css';
import Footer from './ChildComponents/Footer'
import { WelcomePage} from './MainComponents/WelcomePage'
import  WhatIDoPage  from './MainComponents/WhatIDoPage'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      title: "Kimberly Bone",
      headerLinks: [
        { title: "Welcome", path: "/" },
        { title: "What I Do", path: "/what" },
        { title: "Who I Am", path: "/who" },
        { title: "Contact Me", path: "/contact" }
      ],
      welcome: {
        title: "Welcome",
        subTitle: "A little bit about me...",
        subText: "Checkout my projects"
      },
      what: {
        title: "What I Do",
        subTitle: "A little bit about me...",
        subText: "Checkout my projects"
      },
      who: {
        title: "Who I am"
      },
      contact: {
        title: "Let's Connect"
      }
    }
  }

  render() {
    const { headerLinks, welcome, what, who, contact } = this.state
    return(
      <Router>
        <Container className="p-0" fluid={ true }>
          <Navbar className="border-bottom" bg="transparent" expand="lg">

            <Navbar.Brand> Kimberly Bone </Navbar.Brand>
            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle"/>

            <Navbar.Collapse id="navbar-toggle">

              <Nav className='ml-auto'>
                <Link className="nav-link" to="/" > Welcome </Link>
                <Link className="nav-link" to="/what" > What I do </Link>
                <Link className="nav-link" to="/who" > Who I am </Link>
                <Link className="nav-link" to="/contact" > Contact Me </Link>
              </Nav>

            </Navbar.Collapse>

          </Navbar>

          <Route path="/" exact render={ () => <WelcomePage title={ welcome.title } subtitle={ welcome.subTitle  }/> }/>
          <Route path="/" exact render={ () => <WhatIDoPage title={ welcome.title } subtitle={ welcome.subTitle  }/> }/>
          <Route path="/" exact render={ () => <WelcomePage title={ welcome.title } subtitle={ welcome.subTitle  }/> }/>
          <Route path="/" exact render={ () => <WelcomePage title={ welcome.title } subtitle={ welcome.subTitle  }/> }/>

          <Footer></Footer>

        </Container>
      </Router>

    )
  };
}

export default App;
